module.exports = {
    newPassword : (length, chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789') => {
        if (typeof length !== 'number' || length == undefined) return new TypeError('length is not a number: Correct usage: .newPassword(6) or .newPassowd(3) not .newPassword(' + length + ')')
            var result           = '';
            var characters       = chars;
            var charactersLength = characters.length;
            for ( var i = 0; i < length; i++ ) {
                result += characters.charAt(Math.floor(Math.random() *
                    charactersLength));
            }
            return result;
        }
}